import React, { useState, useContext, useEffect } from "react";
import Modal from "react-modal";

// CSS Imports
import "./App.css";
import "./landing-page.css";

const modalStyles = {
  content: {
    width: "30%",
    height: "55%",
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
};

Modal.setAppElement("#root");

const isLetter = (character) => {
  return character.toUpperCase() !== character.toLowerCase();
};

const isNumber = (character) => {
  if (character >= "0" && character <= "9") {
    return true;
  } else {
    return false;
  }
};

const isCap = (character) => {
  if (!isLetter(character)) {
    return false;
  } else {
    if (character.toUpperCase() === character) {
      return true;
    } else {
      return false;
    }
  }
};

const isValidPass = (currPass) => {
  let hasNumber = false;
  let hasCapLetter = false;
  let isLongEnough = false;

  if (currPass.length >= 5) {
    isLongEnough = true;
  }

  for (let i = 0; i < currPass.length; i++) {
    if (isCap(currPass.charAt(i))) {
      hasCapLetter = true;
    } else if (isNumber(currPass.charAt(i))) {
      hasNumber = true;
    }

    if (hasNumber && hasCapLetter && isLongEnough) {
      return true;
    }
  }

  return false;
};

export default function Landing({ history }) {
  // Modal State
  const [modalOpen, setModalOpen] = useState(false);

  const closeModal = () => {
    setModalOpen(false);

    return (
      <div className="main-container">
        <Modal
          isOpen={modalOpen}
          onRequestClose={closeModal}
          style={modalStyles}
          contentLabel="Example Modal"
        >
          <h1 style={{ textAlign: "center", fontSize: "1.5rem" }}>
            Sign Up for Trello Clone!
          </h1>
        </Modal>

        <div className="intro">
          <h1
            style={{
              textAlign: "center",
              color: "white",
              margin: "1rem 0",
              marginTop: "4rem",
              fontSize: "2.5rem",
            }}
          >
            Trello Clone
          </h1>

          <h2
            style={{
              textAlign: "center",
              margin: "1rem 0",
              lineHeight: "1.7rem",
            }}
          >
            Simple.
            <br />
            Organized.
            <br />
            Accessible.
          </h2>

          <h3
            style={{
              textAlign: "center",
              width: "40%",
              fontWeight: "200",
              color: "white",
              fontSize: "1.25rem",
              margin: "1rem 0",
            }}
          >
            Task tracking for your everyday needs
          </h3>
        </div>
        <div className="login">
          <div className="login-box">
            <h1 className="title1">Login</h1>

            <p
              className="else"
              onClick={() => {
                setModalOpen(true);
              }}
            >
              Sign Up Here
            </p>
          </div>
        </div>
      </div>
    );
  };
}
