import React from "react";
import { Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Draggable } from "react-beautiful-dnd";

const useStyle = makeStyles((theme) => ({
  card: {
    padding: theme.spacing(1, 1, 1, 2),
    margin: theme.spacing(1),
  },
}));

export default function Card({ card, index }) {
  const classes = useStyle();

  const bannerImage = document.getElementById("uploadBannerImage");
  const imgData = getBase64Image(bannerImage);
  localStorage.setItem("imgData", imgData);

  var dataImage = localStorage.getItem("imgData");
  const bannerImg = document.getElementById("tableBanner");
  bannerImg.src = "data:image/png;base64," + dataImage;

  function getBase64Image(img) {
    var canvas = document.createElement("canvas");
    window.canvas.width = img.width;
    window.canvas.height = img.height;

    var ctx = canvas.getContext("2d");
    ctx.drawImage(img, 0, 0);

    var dataURL = canvas.toDataURL("image/png");

    return dataURL.replace(/^data:image\/(png|jpg);base64,/, "");
  }

  return (
    <Draggable draggableId={card.id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.dragHandleProps}
          {...provided.draggableProps}
        >
          <Paper className={classes.card}>{card.title}</Paper>
          <input type="file" id="uploadBannerImage" onChange={getBase64Image} />
          <img src="" id="tableBanner" alt="" />
        </div>
      )}
    </Draggable>
  );
}
