import { useDropzone } from "react-dropzone";

const useStyle = makeStyles((theme) => ({
  card: {
    padding: theme.spacing(1, 1, 1, 2),
    margin: theme.spacing(1),
  },
}));

export default function Card({ card, index }) {
  const classes = useStyle();

